PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE android_metadata (locale TEXT);
CREATE TABLE User (id INTEGER PRIMARY KEY AUTOINCREMENT, avatar TEXT, full_name TEXT NOT NULL, hearts INTEGER DEFAULT 0, postal_code TEXT, username TEXT UNIQUE NOT NULL, password_hash TEXT NOT NULL);
CREATE TABLE Recipe (id INTEGER PRIMARY KEY, title TEXT NOT NULL, image TEXT, youtube_url TEXT, ingredients TEXT, instructions TEXT, keywords TEXT);
CREATE TABLE MealPlan (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT NOT NULL, user_id INTEGER NOT NULL, recipes TEXT NOT NULL, goal INTEGER NOT NULL CHECK(goal IN (0,1)), cooked_recipes TEXT, is_private INTEGER NOT NULL DEFAULT 0, FOREIGN KEY(user_id) REFERENCES User(id));
CREATE TABLE HeartedMealPlan (user_id INTEGER NOT NULL, meal_plan_id INTEGER NOT NULL, PRIMARY KEY(user_id, meal_plan_id), FOREIGN KEY(user_id) REFERENCES User(id), FOREIGN KEY(meal_plan_id) REFERENCES MealPlan(id));
DELETE FROM sqlite_sequence;
COMMIT;